import React, { useState, useEffect, useContext } from "react";
import Button from "../timers/shared/button.js";
import DisplayTime from "../timers/shared/DisplayTime.js";
import Panel from "../timers/shared/Panel.js";
import { FaFastForward } from "react-icons/fa";
import { GlobalContext } from "../../App.js";


const Stopwatch = (props) => {
  const [time, setTime] = useState((props.minutes * 60 + props.seconds) * 1000);
  const {
    activeIndex,
    setActiveIndex,
    timerIsRunning,
    timers, 
    setTimers,
  } = useContext(GlobalContext);
  const isActive = props.index === activeIndex;

  useEffect(() => {
    let interval;

    if (isActive && timerIsRunning) {
      interval = setInterval(() => {
        setTime((prevTime) => {
          const newTime = prevTime + 1000;

          // Check if the time has reached the specified duration
          if (newTime >= (props.minutes * 60 + props.seconds) * 1000) {
            // Move on to the next timer in the queue
            handleFastForward();
          }

          return newTime;
        });
      }, 1000);
    } else {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [isActive, activeIndex, timerIsRunning, props.index, props.minutes, props.seconds]);

  const handleFastForward = () => {
    setTime(0);
    setActiveIndex(props.index + 1);
  };

  const handleSetMinutes = (mins) => {
    const timerToEdit = timers[props.index];
    const updatedTimer = {
      ...timerToEdit,
      minutes: mins,
    };
    const timersCopy = [...timers];
    timersCopy.splice(props.index, 1, updatedTimer);
    setTimers(timersCopy);
  };

  const handleSetSeconds = (secs) => {
    const timerToEdit = timers[props.index];
    const updatedTimer = {
      ...timerToEdit,
      seconds: secs,
    };
    const timersCopy = [...timers];
    timersCopy.splice(props.index, 1, updatedTimer);
    setTimers(timersCopy);
  };



  return (
    <div className="stopwatch">
      <Input
        minutes={props.minutes}
        setMinutes={handleSetMinutes}
        seconds={props.seconds}
        setSeconds={handleSetSeconds}
        disabled={isActive}
      />
      <DisplayTime time={time} />
      <Panel>
      <Button onClick={handleFastForward}>
          <FaFastForward />
        </Button>
      </Panel>
    </div>
  );
};

export default Stopwatch;
