import React, { useContext } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { GlobalContext } from "../App.js";
import { FaPlus, FaFastForward } from "react-icons/fa";
import Countdown from "../components/timers/Countdown.js";
import Tabata from "../components/timers/Tabata.js";
import Stopwatch from "../components/timers/Stopwatch.js";
import XY from "../components/timers/XY.js";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

const Timers = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Timer = styled.div`
  background-color: ${({ isActive }) => (isActive ? "black" : "#131313")};
  border-radius: 15px;
  margin: 10px;
  padding: 15px;
  padding-top: 20px;
  font-size: 1.5rem;
  color: darkred;
  width: 100%;
  text-align: center;
`;

const StyledButton = styled.button`
  background-color: darkred;
  color: white;
  padding: 10px 20px;
  margin: 0 10px;
  border: none;
  border-radius: 25px;
  font-size: 1rem;
  font-weight: 400;
  cursor: pointer;

  &:hover {
    background-color: red;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  background-color: black;
  padding: 25px;
  background-color: #121212; 
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.4);
  background-image: 
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%),
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%),
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%),
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%);
  background-size: 30px 30px, 30px 30px, 30px 30px, 30px 30px;
  background-position: 0 0, 10px 10px, 20px 20px, 15px 15px;
`;

const TimerGroup = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 30px;
`;


const AddButton = styled(Link)`
  margin-top: 20px;
  padding: 10px;
  background-color: #4caf50;
  color: white;
  text-decoration: none;
  border-radius: 25px;

  &:hover {
    background-color: darkgreen;
  }
`;

const DraggableTimer = ({ children, index, onDrop }) => {
  const [{ isOver }, drop] = useDrop({
    accept: "TIMER",
    drop: () => onDrop(index),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div ref={drop}>
      <div style={{ opacity: isOver ? 0.5 : 1 }}>{children}</div>
    </div>
  );
};

const DraggableTimerWrapper = ({ index, children }) => {
  const [{ isDragging }, drag] = useDrag({
    type: "TIMER",
    item: { index },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div ref={drag} style={{ opacity: isDragging ? 0.5 : 1 }}>
      {children}
    </div>
  );
};

const renderComponent = (timerItem, index) => {
  let component;
  if (timerItem.componentType === "COUNTDOWN") {
    component = (
      <Countdown
        index={index}
        minutes={timerItem.minutes}
        seconds={timerItem.seconds}
      />
    );
  }
  if (timerItem.componentType === "TABATA") {
    component = (
      <Tabata
        index={index}
        minutes={timerItem.minutes}
        seconds={timerItem.seconds}
        rounds={timerItem.rounds}
      />
    );
  }
  if (timerItem.componentType === "STOPWATCH") {
    component = <Stopwatch index={index}
    minutes={timerItem.minutes}
    seconds={timerItem.seconds} />;
  }
  if (timerItem.componentType === "XY") {
    component = <XY 
    index={index}
    minutes={timerItem.minutes}
    seconds={timerItem.seconds}
    rounds={timerItem.rounds} />;
  }
  return component;
};

const TimersView = () => {
  const {
    activeIndex,
    setActiveIndex,
    isPaused,
    setIsPaused,
    setTimerIsRunning,
    timers,
    setTimers,
  } = useContext(GlobalContext);

  const handleTimerDrop = (index) => {
    if (activeIndex !== index) {
      const updatedTimers = [...timers];
      const [draggedTimer] = updatedTimers.splice(activeIndex, 1);
      updatedTimers.splice(index, 0, draggedTimer);
      setTimers(updatedTimers);
      setActiveIndex(index);
    }
  };

  const handleButtonAction = (action) => {
    switch (action) {
      case "fastForward":
        setActiveIndex(activeIndex + 1);
        setIsPaused(false);
        setTimerIsRunning(true);
        break;
      case "start":
        setActiveIndex(0);
        setIsPaused(false);
        setTimerIsRunning(true);
        break;
      case "pausePlay":
        setIsPaused((prevIsPaused) => !prevIsPaused);
        setTimerIsRunning((prevTimerIsRunning) => !prevTimerIsRunning);
        break;
      default:
        break;
    }
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <ButtonGroup>
          {timers.length > 0 ? (
            <>
              <StyledButton onClick={() => handleButtonAction("fastForward")}>
                <FaFastForward />
              </StyledButton>
              <StyledButton onClick={() => handleButtonAction("start")}>Start</StyledButton>
              <StyledButton onClick={() => handleButtonAction("pausePlay")}>
                {isPaused ? "Play" : "Pause"}
              </StyledButton>
            </>
          ) : null}
        </ButtonGroup>
        <div></div>
        <TimerGroup>
          <Timers>
            {timers.length > 0 ? (
              timers.map((timer, index) => (
                <DraggableTimerWrapper key={index} index={index}>
                  <DraggableTimer index={index} onDrop={handleTimerDrop}>
                    <Timer isActive={activeIndex === index}>{renderComponent(timer, index)}</Timer>
                  </DraggableTimer>
                </DraggableTimerWrapper>
              ))
            ) : (
              <div>Configure your timers!</div>
            )}
            <AddButton to="/add">
              <FaPlus />
            </AddButton>
          </Timers>
        </TimerGroup>
      </div>
    </DndProvider>
  );
};

export default TimersView;
