import React, { useContext, useEffect } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { GlobalContext } from "../App";
import { FaPlus, FaFastForward } from "react-icons/fa";

const Timers = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Timer = styled.div`
  border: 1px solid gray;
  background-color: black;
  border-radius: 30px;
  margin: 10px;
  padding: 20px;
  font-size: 1.5rem;
  color: darkred;
  width: 100%;
  text-align: center;
`;

const StyledButton = styled.button`
  background-color: darkred;
  color: white;
  padding: 10px 20px;
  margin: 0 10px;
  border: none;
  border-radius: 25px;
  font-size: 1rem;
  font-weight: 400;
  cursor: pointer;

  &:hover {
    background-color: red;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  background-color: black;
  padding: 25px;
  background-color: #121212;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.4);
  background-image: 
    // ... (your existing background-image styles)
`;

const TimerGroup = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 30px;
`;

const AddButton = styled(Link)`
  margin-top: 20px;
  padding: 10px;
  background-color: #4caf50;
  color: white;
  text-decoration: none;
  border-radius: 25px;

  &:hover {
    background-color: darkgreen;
  }
`;

const TimersView = () => {
  const {
    activeIndex,
    setActiveIndex,
    isPaused,
    setIsPaused,
    setTimerIsRunning,
    timers,
    setSavedConfiguration,
  } = useContext(GlobalContext);

  const handleFastForward = () => {
    setActiveIndex(activeIndex + 1);
    setIsPaused(false);
    setTimerIsRunning(true);
  };

  const handlePausePlay = () => {
    if (activeIndex === null) {
      setActiveIndex(0);
      setIsPaused(false);
      setTimerIsRunning(true);
    } else {
      setIsPaused((prevIsPaused) => !prevIsPaused);
      setTimerIsRunning(activeIndex !== null && !isPaused);
    }
  };

  const handleSaveConfiguration = (timerItem) => {
    setSavedConfiguration({ ...timerItem });
  };

  useEffect(() => {
    console.log("TimersView - Saved Configuration:", setSavedConfiguration);
  }, [setSavedConfiguration]);

  return (
    <div>
      <ButtonGroup>
        {timers.length > 0 && (
          <>
            <StyledButton onClick={handleFastForward}>
              <FaFastForward />
            </StyledButton>
            <StyledButton onClick={handlePausePlay}>
              {activeIndex === null ? "Start" : isPaused ? "Pause" : "Play"}
            </StyledButton>
          </>
        )}
      </ButtonGroup>
      <TimerGroup>
        <Timers>
          {timers.length > 0 ? (
            timers.map((timer, index) => (
              <Timer key={index}>
                {/* ... (your existing Timer component) */}
                <StyledButton onClick={() => handleSaveConfiguration(timer)}>
                  Save
                </StyledButton>
              </Timer>
            ))
          ) : (
            <div>Configure your timers!</div>
          )}
          <AddButton to="/add">
            <FaPlus />
          </AddButton>
        </Timers>
      </TimerGroup>
    </div>
  );
};

export default TimersView;
