import React, { useState } from "react";
import styled from "styled-components";

const TimerExplanation = () => {
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  return (
    <Container>
      <DropdownButton onClick={toggleDropdown}>
        Timer Information
      </DropdownButton>

      {dropdownVisible && (
        <DropdownContent>
          <TimerSection>
            <TimerTitle>Countdown Timer</TimerTitle>
            <TimerDescription>
              The countdown timer counts down from a specified time to zero.
            </TimerDescription>
          </TimerSection>
          
          <TimerSection>
            <TimerTitle>Tabata Timer</TimerTitle>
            <TimerDescription>
              The Tabata timer follows the Tabata training protocol, with alternating
              intervals of intense exercise and rest.
            </TimerDescription>
          </TimerSection>
          
          <TimerSection>
            <TimerTitle>Stopwatch</TimerTitle>
            <TimerDescription>
              The stopwatch is a simple timer that counts up from zero, measuring
              elapsed time.
            </TimerDescription>
          </TimerSection>

          <TimerSection>
            <TimerTitle>XY Timer</TimerTitle>
            <TimerDescription>
              The XY timer is a custom timer with unique features tailored to specific
              needs.
            </TimerDescription>
          </TimerSection>
        </DropdownContent>
      )}
    </Container>
  );
};

const Container = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  max-width: 600px;
  margin: 0 auto;
`;

const DropdownButton = styled.button`
  background-color: #333;
  color: #fff;
  padding: 10px;
  border: none;
  border-radius: 15px;
  cursor: pointer;
`;

const DropdownContent = styled.div`
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 10px;
  margin-top: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const TimerSection = styled.div`
  margin-bottom: 20px;
`;

const TimerTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 10px;
`;

const TimerDescription = styled.p`
  font-size: 1rem;
  color: #666;
`;

export default TimerExplanation;
