import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  position: relative;
  margin-bottom: 10px;
`;

const DropdownButton = styled.button`
  background-color: #121212;
  color: #fff;
  padding: 10px;
  border: none;
  border-radius: 10px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.4);
  background-image: 
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%),
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%),
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%),
    linear-gradient(135deg, transparent 49.5%, rgba(255, 0, 0, 0.2) 49.5%, rgba(255, 0, 0, 0.2) 50.5%, transparent 50.5%);
  background-size: 30px 30px, 30px 30px, 30px 30px, 30px 30px;
  background-position: 0 0, 10px 10px, 20px 20px, 15px 15px;
  cursor: pointer;
`;

const DropdownContent = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  display: ${({ visible }) => (visible ? "block" : "none")};
  background-color: #121212;
  border: 1px solid #ddd;
  border-radius: 10px;
  padding: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 1;
`;

const TimerSection = styled.div`
  margin-bottom: 20px;
`;

const TimerTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 10px;
  color: #fff;
`;

const TimerDescription = styled.p`
  font-size: 1rem;
  color: #666;
`;

const TimerExplanation = () => {
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  return (
    <Container>
      <DropdownButton onClick={toggleDropdown}>
        Timer Information
      </DropdownButton>

      <DropdownContent visible={dropdownVisible}>
        <TimerSection>
          <TimerTitle>Countdown Timer</TimerTitle>
          <TimerDescription>
            The countdown timer counts down from a specified time to zero.
          </TimerDescription>
        </TimerSection>
        
        <TimerSection>
          <TimerTitle>Tabata Timer</TimerTitle>
          <TimerDescription>
            The Tabata timer follows the Tabata training protocol, with alternating
            intervals of intense exercise and rest.
          </TimerDescription>
        </TimerSection>
        
        <TimerSection>
          <TimerTitle>Stopwatch</TimerTitle>
          <TimerDescription>
            The stopwatch is a simple timer that counts up from zero, measuring
            elapsed time.
          </TimerDescription>
        </TimerSection>

        <TimerSection>
          <TimerTitle>XY Timer</TimerTitle>
          <TimerDescription>
            The XY timer is a custom timer with unique features tailored to specific
            needs.
          </TimerDescription>
        </TimerSection>
      </DropdownContent>
    </Container>
  );
};

export default TimerExplanation;
