import { BrowserRouter as Router, Routes, Route,} from "react-router-dom";
import React, { useState, createContext, useEffect } from "react";
import styled from "styled-components";
import DocumentationView from "./views/DocumentationView";
import TimersView from "./views/TimersView";
import AddView from "./views/AddView";

const Container = styled.div`
  background: #f0f6fb;
  height: 100vh;
  overflow: auto;
`;


export const GlobalContext = createContext(null);

const App = () => {
  const [timers, setTimers] = useState([]);
  const [activeIndex, setActiveIndex] = useState(null);
  const [timerIsRunning, setTimerIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [pausedIndex, setPausedIndex] = useState();

  const handleRemoveTimer = (updatedTimers, removedTimer) => {
    setTimers(updatedTimers);
    console.log("Removed Timer:", removedTimer);
  };

  useEffect(() => {
    console.log("App.js global state active index:", activeIndex);
  }, [activeIndex]);

  useEffect(() => {
    console.log({ timers });
  }, [timers]);

  return (
    <GlobalContext.Provider
      value={{
        timers,
        setTimers,
        timerIsRunning,
        setTimerIsRunning,
        activeIndex,
        setActiveIndex,
        isPaused,
        setIsPaused,
        pausedIndex,
        setPausedIndex,
      }}
    >
      <Container>
        <Router>
          <Routes>
            <Route path="/docs" element={<DocumentationView />} />
            <Route path="/" element={<TimersView />} />
            <Route
              path="/add"
              element={<AddView onRemoveTimer={handleRemoveTimer} />}
            />
          </Routes>
        </Router>
      </Container>
    </GlobalContext.Provider>
  );
};

export default App;
